const rows = items;

// Extraer IDs v√°lidos
const ids = rows
  .map(r => r.json.id_cita)
  .filter(id => typeof id === 'string' && /^CITA-\d+$/.test(id));

// Si no hay IDs v√°lidos, iniciar contador
if (ids.length === 0) {
  return [{
    json: {
      id_cita: 'CITA-001'
    }
  }];
}

// Ordenar y obtener el √∫ltimo
ids.sort((a, b) => {
  const numA = parseInt(a.split('-')[1], 10);
  const numB = parseInt(b.split('-')[1], 10);
  return numA - numB;
});

const lastId = ids[ids.length - 1];
const lastNumber = parseInt(lastId.split('-')[1], 10);
const newNumber = lastNumber + 1;

// Formato final
const newId = `CITA-${String(newNumber).padStart(3, '0')}`;
const chatId = $node["Telegram Trigger"].json.message.chat.id;

return [{
  json: {
    id_cita: newId,
    chatId: chatId
  }
}];

const fecha = $input.first().json.datos_parciales_1; // YYYY-MM-DD
