  return [{
    json: {
      estado: 'Pendiente'
    }
  }];
}

const fechaCita = new Date(`${fecha}T${hora}:00`);

const ahora = new Date();

const estado = fechaCita < ahora ? 'Completada' : 'Pendiente';


return [{
  json: {
    estado
  }
}];

