const listas = $items("Get Listas").map(i => i.json);
const items = $items("Get Items").map(i => i.json);

// Agrupar items por id_lista
const itemsPorLista = {};

items.forEach(i => {
  if (!itemsPorLista[i.id_lista]) {
    itemsPorLista[i.id_lista] = [];
  }
  itemsPorLista[i.id_lista].push(i);
});

// Clasificar listas
const listasPendientes = [];
const listasCompletadas = [];

listas.forEach(lista => {
  const itemsLista = itemsPorLista[lista.id_lista] || [];

  const tienePendientes = itemsLista.some(i => i.estado === 'Pendiente');

  if (tienePendientes) {
    listasPendientes.push({ ...lista, items: itemsLista });
  } else if (itemsLista.length > 0) {
    listasCompletadas.push({ ...lista, items: itemsLista });
  }
});

// Construir mensaje
let mensaje = 'üìã *Resumen de listas*\n\n';

// LISTAS PENDIENTES
mensaje += 'üìÖ *Listas pendientes*\n\n';

if (listasPendientes.length === 0) {
  mensaje += 'No tienes listas pendientes.\n\n';
} else {
  listasPendientes.forEach((lista, index) => {
    mensaje +=
      `${index + 1}. *${lista.nombre_lista}*\n` +
      `Tipo: ${lista.tipo}\n`;

    lista.items
      .filter(i => i.estado === 'Pendiente')
      .forEach(i => {
        mensaje += `- ${i.item}\n`;
      });

    mensaje += '\n';
  });
}

// LISTAS COMPLETADAS
mensaje += '‚úÖ *Listas completadas*\n\n';

if (listasCompletadas.length === 0) {
  mensaje += 'No tienes listas completadas.\n';
} else {
  listasCompletadas.forEach((lista, index) => {
    mensaje +=
      `${index + 1}. *${lista.nombre_lista}*\n` +
      `Tipo: ${lista.tipo}\n`;

    lista.items.forEach(i => {
      mensaje += `- ${i.item}\n`;
    });

    mensaje += '\n';
  });
}

// Telegram chat id
